def adicionar(a,b):
    """Retorna um valor resultante da soma dos dois valores"""
    return a + b

def subtrair(a,b):
    """Retorna um valor resultante da subtração dos dois valores"""
    return a - b

def multiplicar(a,b):
    """Retorna um valor resultante da multiplicação dos dois valores"""
    return a * b

def dividir(a,b):
    """Retorna um valor resultante da divisão dos dois valores"""
    return a / b