def adicionar(a, b):
    """
    retorna o valor resultante da adição dos dois valores
    """
    return a + b


def subtrair(a, b):
    """
    retorna o valor resultante da subtração de a por b
    """
    return a - b


def multiplicar(a, b):
    """
    retorna o valor resultante da multiplicação dos dois valores
    """
    return a * b

def dividir(a, b):
    """
    retorna o valor resultante da divisão de a por b
    """
    return a / b